<?php

namespace TheRestartProject\RepairDirectory\Domain\Models;

/**
 * Class Business
 *
 * @category Class
 * @package  TheRestartProject\RepairDirectory\Domain\Models
 * @author   Joaquim d'Souza <joaquim@outlandish.com>
 * @license  http://www.gnu.org/copyleft/gpl.html GNU General Public License
 * @link     http://www.outlandish.com/
 */
class Business
{

    /**
     * The business ID (generated by the infrastructure layer)
     *
     * @var int
     */
    private $uid;

    /**
     * The common name of the business
     *
     * @var string
     */
    private $name;

    /**
     * The address of the business, not including postcode
     *
     * @var string
     */
    private $address;

    /**
     * The postcode of the business
     *
     * @var string
     */
    private $postcode;

    /**
     * Return the business ID
     *
     * @return int
     */
    public function getId()
    {
        return $this->uid;
    }

    /**
     * Return the business name
     *
     * @return string
     */
    public function getName()
    {
        return $this->name;
    }

    /**
     * Set the business name
     *
     * @param string $name The value to set
     *
     * @return void
     */
    public function setName($name)
    {
        $this->name = $name;
    }

    /**
     * Return the business address
     *
     * @return string
     */
    public function getAddress()
    {
        return $this->address;
    }

    /**
     * Set the business address
     *
     * @param string $address The value to set
     *
     * @return void
     */
    public function setAddress($address)
    {
        $this->address = $address;
    }

    /**
     * Return the business postcode
     *
     * @return string
     */
    public function getPostcode()
    {
        return $this->postcode;
    }

    /**
     * Set the business postcode
     *
     * @param string $postcode The value to set
     *
     * @return void
     */
    public function setPostcode($postcode)
    {
        $this->postcode = $postcode;
    }

}