<?php

namespace TheRestartProject\RepairDirectory\Domain\Models;

/**
 * Class Region
 *
 * @category Model
 * @package  TheRestartProject\RepairDirectory\Domain\Models
 * @license  http://www.gnu.org/copyleft/gpl.html GNU General Public License
 *
 * @SuppressWarnings(PHPMD.ExcessivePublicCount)
 * @SuppressWarnings(PHPMD.ExcessiveClassComplexity)
 */
class Region
{
    /**
     * The region ID (generated by the infrastructure layer)
     *
     * @var int
     */
    private $uid;

    /**
     * The name of the region
     *
     * @var string
     */
    private $name;

    /**
     * The geometry of the region, in WKT format.  This must be a POLYGON, not a MULTIPOLYGON.
     *
     * @var string
     */
    private $polygon;

    /**
     * Return the region's unique id
     *
     * @return int
     */
    public function getUid()
    {
        return $this->uid;
    }

    /**
     * Return the region name
     *
     * @return string
     */
    public function getName()
    {
        return $this->name;
    }

    /**
     * Return the region polygon
     *
     * @return string
     */
    public function getPolygon()
    {
        return $this->polygon;
    }
}
